import { useState, useEffect } from 'react';
import StarRating from './StarRating';

function Modal({ isOpen, message, onClose }) {
  if (!isOpen) return null;
  return (
    <div className='modal'>
      <div className='modal-content'>
        <span className='close' onClick={onClose}>
          &times;
        </span>
        <p>{message}</p>
        <button onClick={onClose}>關閉</button>
      </div>
    </div>
  );
}

// API base URL
const API_URL = 'https://imdb.iamidiotareyoutoo.com/search';

const average = (arr) =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);

export default function App() {
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState([]);
  const [query, setQuery] = useState('');
  const [totalResults, setTotalResults] = useState(0);
  const [modalOpen, setModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  const closeModal = () => setModalOpen(false);

  const handleAddWatched = (movie) => {
    const alreadyRated = watched.find(
      (watchedMovie) => watchedMovie['#IMDB_ID'] === movie['#IMDB_ID']
    );
    if (alreadyRated) {
      setModalOpen(true);
      setModalMessage('You already rated this movie');
    } else {
      setWatched((watched) => [...watched, movie]);
    }
  };

  useEffect(() => {
    const fetchMovies = async () => {
      if (query.length < 3) {
        setMovies([]);
        setTotalResults(0);
        return;
      }
      try {
        const res = await fetch(`${API_URL}?q=${encodeURIComponent(query)}`);
        const data = await res.json();
        if (data.ok && Array.isArray(data.description)) {
          setMovies(data.description);
          setTotalResults(data.description.length);
        } else {
          throw new Error('Invalid data format received from API');
        }
        setMovies(data.description);
      } catch (err) {
        console.error(err);
      }
    };
    fetchMovies();
  }, [query]);

  return (
    <>
      <NavBar>
        <Search query={query} setQuery={setQuery} />
        <NumResults totalResults={totalResults} />
      </NavBar>

      <Main>
        {/* 法1 */}
        <Box>
          <MovieList movies={movies} onAddWatched={handleAddWatched} />
        </Box>
        <Box>
          <WatchedSummary watched={watched} />
          <WatchedMoviesList watched={watched} />
          <Modal
            isOpen={modalOpen}
            message={modalMessage}
            onClose={closeModal}
          />
        </Box>

        {/* 法2 */}
        {/* <Box element={<MovieList movies={movies} />} />
        <Box
          element={
            <>
              <WatchedSummary watched={watched} />
              <WatchedMoviesList watched={watched} />
            </>
          }
        /> */}
      </Main>
    </>
  );
}

function NavBar({ children }) {
  return (
    <nav className='nav-bar'>
      <Logo />
      {children}
    </nav>
  );
}
function Logo() {
  return (
    <div className='logo'>
      <span role='img'>🍿</span>
      <h1>USE POPCORN</h1>
    </div>
  );
}

function NumResults({ totalResults }) {
  return (
    <p className='num-results'>
      Found <strong>{totalResults}</strong> results
    </p>
  );
}
function Search({ query, setQuery }) {
  return (
    <input
      className='search'
      type='text'
      placeholder='Search movies...'
      value={query}
      onChange={(e) => setQuery(e.target.value)}
    />
  );
}

function Main({ children }) {
  return <main className='main'>{children}</main>;
}

function Box({ children }) {
  const [isOpen, setIsOpen] = useState(true);
  return (
    <div className='box'>
      <button className='btn-toggle' onClick={() => setIsOpen((open) => !open)}>
        {isOpen ? '–' : '+'}
      </button>
      {isOpen && children}
    </div>
  );
}

// function WatchedBox() {
//   const [watched, setWatched] = useState(tempWatchedData);
//   const [isOpen2, setIsOpen2] = useState(true);

//   return (
//     <div className='box'>
//       <button
//         className='btn-toggle'
//         onClick={() => setIsOpen2((open) => !open)}
//       >
//         {isOpen2 ? '–' : '+'}
//       </button>
//       {isOpen2 && (
//         <>
//           <WatchedSummary watched={watched} />
//           <WatchedMoviesList watched={watched} />
//         </>
//       )}
//     </div>
//   );
// }
function MovieList({ movies, onAddWatched }) {
  return (
    <ul className='list'>
      {movies?.map((movie) => (
        <Movie
          movie={movie}
          key={movie['#IMDB_ID']}
          onAddWatched={onAddWatched}
        />
      ))}
    </ul>
  );
}

function Movie({ movie, onAddWatched }) {
  const [userRating, setUserRating] = useState(0);

  const handleRating = (rating) => {
    setUserRating(rating);
    onAddWatched({ ...movie, userRating: rating });
  };
  return (
    <li>
      <img src={movie['#IMG_POSTER']} alt={`${movie['#TITLE']} poster`} />
      <h3>{movie['#TITLE']}</h3>
      <div>
        <p>
          <span>📅</span>
          <span>{movie['#YEAR']}</span>
        </p>
        <StarRating
          maxRating={5}
          messages={['超級難看', '不好看', '還行', '很好看', '超級好看']}
          defaultRating={userRating}
          onRate={handleRating}
        />
      </div>
    </li>
  );
}

