function MovieList({ movies, onAddWatched }) {
  return (
    <ul className='list'>
      {movies?.map((movie) => (
        <Movie
          movie={movie}
          key={movie['#IMDB_ID']}
          onAddWatched={onAddWatched}
        />
      ))}
    </ul>
  );
}

function Movie({ movie, onAddWatched }) {
  const [userRating, setUserRating] = useState(0);

  const handleRating = (rating) => {
    setUserRating(rating);
    onAddWatched({ ...movie, userRating: rating });
  };
  return (
    <li>
      <img src={movie['#IMG_POSTER']} alt={`${movie['#TITLE']} poster`} />
      <h3>{movie['#TITLE']}</h3>
      <div>
        <p>
          <span>🗓</span>
          <span>{movie['#YEAR']}</span>
        </p>
        <StarRating
          maxRating={5}
          messages={['超難看', '不好看', '還行', '好看', '超好看']}
          defaultRating={userRating}
          onRate={handleRating}
        />
      </div>
    </li>
  );
}

function WatchedMoviesList({ watched }) {
  return (
    <ul className='list'>
      {watched.map((movie) => (
        <WatchedMovie movie={movie} key={movie['#IMDB_ID']} />
      ))}
    </ul>
  );
}

function WatchedMovie({ movie }) {
  return (
    <li>
      <img src={movie['#IMG_POSTER']} alt={`${movie['#TITLE']} poster`} />
      <h3>{movie['#TITLE']}</h3>
      <div>
        <p>
          <span>🏆</span>
          <span>{movie['#RANK']}</span>
        </p>
        <p>
          <span>🌟</span>
          <span>{movie.userRating}</span>
        </p>
        <p>
          <span>⏳</span>
          <span>{movie.runtime} min</span>
        </p>
      </div>
    </li>
  );
}
function WatchedSummary({ watched }) {
  const avgImdbRating = average(watched.map((movie) => movie.imdbRating));
  const avgUserRating = average(watched.map((movie) => movie.userRating));
  const avgRuntime = average(watched.map((movie) => movie.runtime));
  return (
    <div className='summary'>
      <h2>Movies you watched</h2>
      <div>
        <p>
          <span>#️⃣</span>
          <span>{watched.length} movies</span>
        </p>
        <p>
          <span>⭐️</span>
          <span>{avgImdbRating}</span>
        </p>
        <p>
          <span>🌟</span>
          <span>{avgUserRating}</span>
        </p>
        <p>
          <span>⏳</span>
          <span>{avgRuntime} min</span>
        </p>
      </div>
    </div>
  );
}
