import { useState, useEffect } from 'react';
import StarRating from './StarRating';

// API base URL
const API_URL = 'https://imdb.iamidiotareyoutoo.com/search';

export default function App() {
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState([]);
  const [query, setQuery] = useState('');
  const [totalResults, setTotalResults] = useState(0);
  const [isLoading, setIsLoading] = useState(false);

  const handleAddWatched = (movie) => {
    setWatched((watched) => {
      const existingMovie = watched.find(
        (m) => m['#IMDB_ID'] === movie['#IMDB_ID']
      );
      if (existingMovie) {
        return watched.map((m) =>
          m['#IMDB_ID'] === movie['#IMDB_ID']
            ? { ...m, userRating: movie.userRating }
            : m
        );
      } else {
        return [...watched, movie];
      }
    });
  };

  useEffect(() => {
    const fetchMovies = async () => {
      if (query.length < 1) {
        setMovies([]);
        setTotalResults(0);
        setIsLoading(false);
        return;
      }
      try {
        setIsLoading(true);
        const res = await fetch(
          `${API_URL}?q=${encodeURIComponent(query.trim())}`
        );
        const data = await res.json();
        if (data.ok && Array.isArray(data.description)) {
          setMovies(data.description);
          setTotalResults(data.description.length);
        } else {
          throw new Error('Invalid data format received from API');
        }
      } catch (err) {
        console.error(err);
      } finally {
        setIsLoading(false);
      }
    };
    const timeoutId = setTimeout(fetchMovies, 500); // 添加防抖
    return () => clearTimeout(timeoutId);
  }, [query]);

  return (
    <>
      <NavBar>
        <Search query={query} setQuery={setQuery} />
        <NumResults totalResults={totalResults} />
      </NavBar>

      <Main>
        {/* 法1 */}

        <Box>
          <MovieList
            movies={movies}
            onAddWatched={handleAddWatched}
            loading={isLoading}
          />
        </Box>
        <Box>
          <WatchedSummary watched={watched} />
          <WatchedMoviesList watched={watched} />
        </Box>

        {/* 法2 */}
        {/* <Box element={<MovieList movies={movies} />} />
        <Box
          element={
            <>
              <WatchedSummary watched={watched} />
              <WatchedMoviesList watched={watched} />
            </>
          }
        /> */}
      </Main>
    </>
  );
}

function NavBar({ children }) {
  return (
    <nav className='nav-bar'>
      <Logo />
      {children}
    </nav>
  );
}
function Logo() {
  return (
    <div className='logo'>
      <span role='img'>🍿</span>
      <h1>USE POPCORN</h1>
    </div>
  );
}

function NumResults({ totalResults }) {
  return (
    <p className='num-results'>
      Found <strong>{totalResults}</strong> results
    </p>
  );
}
function Search({ query, setQuery }) {
  return (
    <input
      className='search'
      type='text'
      placeholder='Search movies...'
      value={query}
      onChange={(e) => setQuery(e.target.value)}
    />
  );
}

function Main({ children }) {
