function MovieList({ movies, onAddWatched, loading }) {
  return (
    <ul className='list'>
      {loading ? (
        <div className='loader'>
          <div className='circle'>
            <div className='dot'></div>
            <div className='outline'></div>
          </div>
          <div className='circle'>
            <div className='dot'></div>
            <div className='outline'></div>
          </div>
          <div className='circle'>
            <div className='dot'></div>
            <div className='outline'></div>
          </div>
          <div className='circle'>
            <div className='dot'></div>
            <div className='outline'></div>
          </div>
        </div>
      ) : (
        movies?.map((movie) => (
          <Movie
            movie={movie}
            key={movie['#IMDB_ID']}
            onAddWatched={onAddWatched}
          />
        ))
      )}
    </ul>
  );
}

function Movie({ movie, onAddWatched }) {
  const [userRating, setUserRating] = useState(0);
  const [imageLoaded, setImageLoaded] = useState(false);

  const handleRating = (rating) => {
    setUserRating(rating);
    onAddWatched({ ...movie, userRating: rating });
  };

  const handleImageLoad = () => {
    setImageLoaded(true);
  };

  return (
    <li>
      <div
        style={{ position: 'relative', width: '100%', paddingBottom: '150%' }}
      >
        {!imageLoaded && (
          <div className='loader'>
            <div className='circle dot outline'></div>
          </div>
        )}
        <img
          src={movie['#IMG_POSTER']}
          alt={`${movie['#TITLE']} poster`}
          onLoad={handleImageLoad}
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            display: imageLoaded ? 'block' : 'none',
          }}
        />
      </div>
      <h3>{movie['#TITLE']}</h3>
      <div>
        <p>
          <span>📅</span>
          <span>{movie['#YEAR']}</span>
        </p>
        <StarRating
          maxRating={5}
          messages={['超級難看', '不好看', '還行', '很好看', '超級好看']}
          defaultRating={userRating}
          onRate={handleRating}
        />
      </div>
    </li>
  );
}

